class Modal{constructor(){this.initModal()}initModal=()=>{let modal=document.querySelector(".modal");if(modal===undefined||modal===null){this.buildModal()}this.hideModal()};buildModal=()=>{let modal=document.createElement("div"),headline=document.createElement("h2"),p=document.createElement("p"),aDonate=document.createElement("a"),aRepo=document.createElement("a"),button=document.createElement("button");headline.innerText="Demo";p.innerHTML="This is free, open-source software.<br> If you'd like to support the development of future projects,<br> or say thanks for this one, you can donate.";aDonate.href="https://www.paypal.me/buddenbrock";aDonate.innerText="Donate";aDonate.setAttribute("target","_blank");aRepo.href="https://github.com/Buddenbrock/";aRepo.innerText="Go to Repo";aRepo.setAttribute("target","_blank");modal.classList.add("modal");modal.appendChild(headline);modal.appendChild(p);modal.appendChild(aDonate);modal.appendChild(aRepo);modal.appendChild(button);document.body.appendChild(modal)};hideModal=()=>{let modal=document.querySelector(".modal");let button=modal.querySelector("button");button.addEventListener("click",()=>{modal.classList.add("d-none")})}}new Modal;class Menu{constructor(){this.initMenu()}initMenu=()=>{let button=document.querySelector(".menu-toggle");button.addEventListener("click",()=>{button.classList.toggle("opend")})}}new Menu;